void Slots::Update()
{
	const int chance00 = chance0;
	const int chance01 = chance00 + chance1;
	const int chance02 = chance01 + chance2;
	const int chance03 = chance02 + chance3;
	const int chance04 = chance03 + chance4;
	const int chance05 = chance04 + chance5;

	if (rollnum0 > 0 && rollnum0 <= chance00)
		slot1 = 1;
	else if (rollnum0 > chance00 && rollnum0 <= chance01)
		slot1 = 2;
	else if (rollnum0 > chance01 && rollnum0 <= chance02)
		slot1 = 3;
	else if (rollnum0 > chance02 && rollnum0 <= chance03)
		slot1 = 4;
	else if (rollnum0 > chance03 && rollnum0 <= chance04)
		slot1 = 5;
	else if (rollnum0 > chance04 && rollnum0 <= chance05)
		slot1 = 6;

	if (rollnum1 > 0 && rollnum1 <= chance00)
		slot2 = 1;
	else if (rollnum1 > chance00 && rollnum1 <= chance01)
		slot2 = 2;
	else if (rollnum1 > chance01 && rollnum1 <= chance02)
		slot2 = 3;
	else if (rollnum1 > chance02 && rollnum1 <= chance03)
		slot2 = 4;
	else if (rollnum1 > chance03 && rollnum1 <= chance04)
		slot2 = 5;
	else if (rollnum1 > chance04 && rollnum1 <= chance05)
		slot2 = 6;

	if (rollnum2 > 0 && rollnum2 <= chance00)
		slot3 = 1;
	else if (rollnum2 > chance00 && rollnum2 <= chance01)
		slot3 = 2;
	else if (rollnum2 > chance01 && rollnum2 <= chance02)
		slot3 = 3;
	else if (rollnum2 > chance02 && rollnum2 <= chance03)
		slot3 = 4;
	else if (rollnum2 > chance03 && rollnum2 <= chance04)
		slot3 = 5;
	else if (rollnum2 > chance04 && rollnum2 <= chance05)
		slot3 = 6;
}

void Slots::UpdateCredit()
{
	if (slot1 == 1)
	{
		if(slot2 == 1)
		{
			if (slot3 == 1)
				credits += win000 * rollCost;
			else
				credits += win00x * rollCost;
		}
	else
		credits += win0xx * rollCost;
	}
	if (slot1 == 2 && slot2 == 2)
	{
		if (slot3 == 2)
			credits += win111 * rollCost;
		else
			credits += win11x * rollCost;
	}
	if (slot1 == 3 && slot2 == 3 && slot3 == 3)
	{
			credits += win222 * rollCost;
	}
	if (slot1 == 4 && slot2 == 4 && slot3 == 4)
	{
			credits += win333 * rollCost;
	}
	if (slot1 == 5 && slot2 == 5 && slot3 == 5)
	{
			credits += win444 * rollCost;
	}
	if (slot1 == 6 && slot2 == 6 && slot3 == 6)
	{
		credits += win555 * rollCost;
	}
}

void Slots::UpdateFrame()
{
	if (creditsDisplayed > credits)
		creditsDisplayed = credits;
	if (creditsDisplayed < credits)
	{
		cc = Colors::Green;
		++creditsDisplayed;
	}
	else
		cc = Colors::White;

	if (rgb >= 250)
		plus_rgb = false;
	if (rgb <= 50)
		plus_rgb = true;
	if (plus_rgb)
		rgb += 3;
	else
		rgb -= 3;
}

